  .CheckRun-header .btn.btn-link:not([disabled]) .octicon,
  .CheckStep .log-line-error .CheckStep-line-content, .CheckStep-line:hover,
  .CheckStep[open] .CheckStep-header-label, .CheckStep .log-download-error,
  .CheckStep[open] .CheckStep-header, .CheckRun-header-counter,
  .CheckRun-search-icon, .CheckRun-header .btn-link:hover:not([disabled]),
  .CheckRun-header .btn.btn-link:hover:not([disabled]) .octicon {
    color: #ccc !important;
  }
  .CheckStep-header:hover, .CheckStep[open] .CheckStep-header,
  .CheckStep-line:hover,
  .CheckRun-search details[open] .CheckStep-header-dropdown,
  .CheckStep-header-dropdown:hover {
