  .CheckRun {
    border-top: 1px solid #343434;
  }
  .CheckRun-header, .dropdown-menu-dark, .dropdown-menu-dark .dropdown-divider {
    border-color: #343434 !important;
  }
  .CheckRun-header .CheckRun-search-input {
