/* ==UserStyle==
@name        GitHub Dark
@namespace   StylishThemes
@version     4.2.186
@homepageURL https://github.com/StylishThemes/GitHub-Dark
@updateURL   https://stylishthemes.github.io/GitHub-Dark/github-dark.user.css
@license     BSD-2-Clause
@author      StylishThemes
@advanced color base-color "Base color" #4f8cc9
@advanced color bg-color "Background Color" #202020
@advanced dropdown bg-choice "Background" {
  crosshatch "Cross-Hatch" <<<EOT
  url(data:image/png;base64,) EOT;
  uniform "Uniform Tile" <<<EOT
  url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAABkAQMAAABKLAcXAAAABlBMVEUfHx8aGhrh02scAAAAH0lEQVR4AWMQRAICeHn/kcAH8vSN6hvVN6pvVN+oPgAPQ4LItqOMdQAAAABJRU5ErkJggg==) EOT;
  random "Random Tile" <<<EOT
  url(data:image/png;base64,) EOT;
  custom "Custom" <<<EOT
  var(--ghd-bg-custom) EOT;
  none "None" <<<EOT
  none EOT;
}
@advanced text bg-custom "Custom Background" url(data:image/png;base64,)
@advanced dropdown bg-options "Background image type" {
  Tiled "Tiled" <<<EOT
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important; EOT;
  Fit window size "Fit window size" <<<EOT
  background-repeat: no-repeat !important;
  background-size: cover !important;
  background-position: center top !important; EOT;
}
@advanced dropdown bg-attachment "Background image attachment" {
  Scroll "Scroll" <<<EOT
  scroll EOT;
  Fixed "Fixed" <<<EOT
  fixed EOT;
}
@advanced dropdown syntax-theme "Github syntax theme" {
  Twilight "Twilight" <<<EOT
  /*! GitHub: Twilight *\/:root{--ghd-code-background:#141414;--ghd-code-color:#ccc}.pl-c,.pl-c span{color:#5f5a60!important}.pl-c1,.pl-cce,.pl-cn,.pl-coc{color:#cf6a4c!important}.pl-cos{color:#8f9d6a!important}.pl-e,.pl-ef,.pl-en{color:#ac885b!important}.pl-enc{color:#cf6a4c!important}.pl-enf,.pl-enm{color:#ac885b!important}.pl-ens{color:#cf6a4c!important}.pl-ent{color:#cda869!important}.pl-entc,.pl-enti{color:#ac885b!important}.pl-entm{color:#7587a6!important}.pl-eoa{color:#cda869!important}.pl-eoac,.pl-eoac .pl-pde{color:#7587a6!important}.pl-eoai,.pl-eoai .pl-pde{color:#cda869!important}.pl-eoi{color:#ac885b!important}.pl-k{color:#cf6a4c!important}.pl-ko,.pl-kolp{color:#cda869!important}.pl-kos,.pl-kou{color:#cf6a4c!important}.pl-mai .pl-sf{color:#7587a6!important}.pl-mb{color:#8f9d6a!important}.pl-mc{color:#cda869!important}.pl-mh,.pl-mh .pl-pdh{color:#cf6a4c!important}.pl-mi{color:#cda869!important}.pl-ml{color:#8f9d6a!important}.pl-mm{color:#7587a6!important}.pl-mp{color:#c5af75!important}.pl-mp1 .pl-sf{color:#dad085!important}.pl-mq{color:#cf6a4c!important}.pl-mr,.pl-ms{color:#cda869!important}.pl-pdb{color:#8f9d6a!important}.pl-pdc{color:#5f5a60!important}.pl-pdc1,.pl-pde{color:#cf6a4c!important}.pl-pdi{color:#cda869!important}.pl-pds{color:#8f9d6a!important}.pl-pdv{color:#7587a6!important}.pl-pse,.pl-pse .pl-s2{color:#cf6a4c!important}.pl-s{color:#f9ee98!important}.pl-s1,.pl-s2{color:#ccc!important}.pl-mp .pl-s3{color:#cda869!important}.pl-s3,.pl-sc{color:#dad085!important}.pl-scp{color:#cf6a4c!important}.pl-sf{color:#dad085!important}.pl-smc{color:#ac885b!important}.pl-smi,.pl-smp{color:#ccc!important}.pl-sok{color:#cda869!important}.pl-sol{color:#8f9d6a!important}.pl-som,.pl-sr{color:#7587a6!important}.pl-sra,.pl-src,.pl-sre,.pl-st{color:#cda869!important}.pl-stj{color:#7587a6!important}.pl-stp,.pl-sv,.pl-v{color:#9b859d!important}.pl-vi{color:#cf6a4c!important}.pl-vo{color:#ac885b!important}.pl-vpf{color:#7587a6!important}.pl-mdht,.pl-mi1{color:#55a532!