  body[class="page-responsive"] .form-hr {
    border-bottom-color: var(--color-border-primary, #343434);
  }
  body[class="page-responsive"] .listgroup {
    border-color: var(--color-border-primary, #343434);
  }
  body[class="page-responsive"] .listgroup-item + .listgroup-item {
    border-top-color: var(--color-border-primary, #343434);
  }
  body[class="page-responsive"] .listgroup-header {
    border-bottom-color: var(--color-border-primary, #343434);
  }
  body[class="page-responsive"] .listgroup-overflow {
    background-color: var(--color-bg-secondary, #222);
  }
  @keyframes toggle-color {
    0% {
      background-color: initial;
    }
    to {
      background-color: initial;
    }
  }
  body[class="page-responsive"] .repository-merge-features .form-group.errored .error {
    background-color: transparent;
  }
  body[class="page-responsive"] .user-identification-questions.redesign .topic-input-container .tag-input {
    border-bottom-color: #f0f0f0;
  }
  body[class="page-responsive"] .draft-tag {
    color: #2c2c2c;
  }
  body[class="page-responsive"] .showcase-page-pattern::after {
    background-image: linear-gradient(180deg, hsla(0, 0%, 0%, .85), #181818);
