  body[class="page-responsive"] .actions-workflow-navigation .row-section {
    background-color: transparent !important;
  }
  body[class="page-responsive"] .actions-workflow-navigation .row-parent {
    background-color: transparent;
  }
  body[class="page-responsive"] .actions-workflow-navigation .row-parent:hover {
    background-color: initial;
  }
