:root {
	--dark0: #000;
	--dark25: #111;
	--dark50: #181818;
	--dark75: #1b1b1b;
	--dark100: #222;
	--dark125: #242424;
	--dark150: #282828;
	--dark200: #333;

	--text-primary: #ddd;
	--text-secondary: #999;

	--border-light: #fff1;
	--border-medium: #fff2;
	--border-medium: #fff4;

	--shadow-light: #0002;
	--shadow-heavy: #0008;

	--link: #2183de;

	--icon-opacity: 0.75
}







/* Scrollbar */
body::-webkit-scrollbar {
	width: 15px !important;
	height: 15px !important;
	display: block !important;
	background: #141414 !important;
	background-image: url(data:image/svg+xml;base64,), url(data:image/svg+xml;base64,) !important;
	background-position: top center, bottom center !important;
	background-size: contain !important;
	background-repeat: no-repeat !important;
}

/* Thumb */
body::-webkit-scrollbar-thumb {
	background: #fff4 !important;
	border-left: 2px solid !important;
	border-right: 2px solid !important;
	border-color: #141414 !important;
}

body::-webkit-scrollbar-thumb:hover {
	background: #fff5 !important;
}

[dark] body::-webkit-scrollbar-thumb:active {
	background: #fff6 !important;
}

/* Buttons */
body::-webkit-scrollbar-button:single-button {
	background-position: center center;
	background-size: contain;
  display: block;
  height: 16px;
  width: 16px;
	background: #14141488;
}

body::-webkit-scrollbar-button:single-button:hover {
	background: #14141444;
}

[dark] body::-webkit-scrollbar-button:single-button:active {
	background: #14141422;
}

