    border-color: var(--blue-4) !important;
  }
  div[style*="background-color: lightyellow"] {
    background-color: var(--brown-1) !important;
  }
  div[style*="border: 0.1em solid olive"] {
    border-color: var(--brown-2) !important;
  }
  div[style*="background-color: lightyellow"] div h2 {
    background-color: var(--brown-1) !important;
  }
  div[style*="ackground-color: palegreen"] {
    background-color: var(--green-4) !important;
  }
  div[style*="background-color: #FFEA00; color: black"] {
    color: var(--gray-1) !important;
  }
  #wpTextbox1 {
    color: var(--gray-b);
  }
  #pagehistory li {
    border-color: var(--gray-5);
  }

body {
    background: var(--gray-18);
    color: var(--gray-c);
  }
  .top-nav, .header-home, .focus-block, .card-vertical.card-person,
  .card-vertical.card-person .card-content {
    background: var(--gray-18);
  }
  .logo-container a, .language-dropdown button img[src*="down"],
  .connect-container .icon, img[src*="Wikimedia-Foundation-trademarked-logo"],
  img[src*="Wikipedia_wordmark"] {
    filter: invert(1) brightness(80%);
  }
  .language-list, .home-project-list-item--gray {
    background: var(--gray-28);
  }
  .language-dropdown button {
    color: var(--blue-1);
    background: var(--pink-0);
    border: 1px solid var(--pink-1);
  }
  .language-dropdown button:hover {
    border: 1px solid var(--pink-3);
  }
  .donate-btn {
    background: var(--pink-1);
    border: 1px solid var(--pink-2);
    color: var(--pink-3);
  }
  .donate-btn:hover {
    border: 1px solid var(--pink-3);
  }
  .shadow, .pagination-container,
  .header-home.featured-photo--content-left .page-intro-text .w-32p {
    border-color: var(--gray-5);
  }
  .language-list ul li a.selected {
    border-color: var(--blue-1);
  }
  button img[src*="language"] {
    filter: grayscale(1) brightness(325%);
  }
  .donate-btn img[src*="lock-pink"] {
    filter: brightness(800%);
  }
  .main-nav {
    border-top: 1px solid var(--gray-5);
  }
  .main-nav a {
    background-image: linear-gradient(to top, var(--gray-18), var(--gray-1));
  }
  .header-default .post-meta a {
    background-image: linear-gradient(to top, var(--gray-18), var(--gray-1));
    color: var(--gray-a);
    border-color: var(--blue-1);
  }
  .toc-link.-active, .people .person-name {
    color: var(--gray-e);
  }
  .header-default ul li a, .header-home ul li a, .bg-ltgray .color-gray,
  .language-list ul li a {
    color: var(--gray-c);
  }
  .module-mu .email-signup, .bg-ltgray .related-news .card .date, .color-gray,
  .article-img .img-caption, .translation-bar li,
  .header-home.featured-photo--content-left .page-intro-text .secure {
    color: var(--gray-9a);
  }
  .module-mu .email-signup input::placeholder, .nav-newer, .nav-older {
    color: var(--gray-69);
  }
  .nav-newer svg, .nav-older svg {
    fill: var(--gray-69);
  }
  #News\ Index path[fill="#FFFFFF"] {
    fill: var(--gray-18);
  }
  .nav-newer a:hover svg, .nav-older a:hover svg,
  .elsewhere-wikimedia .link-external a:hover .icon {
    fill: var(--base-color);
  }
  .elsewhere-wikimedia .link-external a .icon {
    fill: var(--gray-c);
    transition: .3s all;
  }
  .header-default ul li.current_page_item a,
  footer ul:not(.social-list) li a:hover {
    color: var(--base-color);
    border-color: var(--base-color);
  }
  .header-default.header-pink .ungrid {
    background: var(--pink-0);
  }
  .cta-primary {
    background: var(--pink-1);
  }
  .cta-primary .h2, .cta-description {
    color: var(--pink-3);
  }
  .module-mu.w-23p .h2.no-border span, .module-mu.w-50p .h2.no-border span {
    color: var(--purple-2);
  }
  .header-default .ungrid .content,
  .connect-container .email-signup #wmf-subscribe-input-email {
    background: var(--gray-18);
  }
  .page-number-list a:hover, .page-number-list span:hover {
    background: var(--gray-3);
  }
  .page-number-list li.active a, .page-number-list .current {
    background: var(--base-color);
  }
  .main-nav a:hover, .wysiwyg p a, .wysiwyg .p a, .wysiwyg li a, .arrow-link,
  .bg-white .related-news .card .card-heading:hover h3,
  .bg-white .related-news .card .card-heading .date h3,
  .module-mu .email-signup input, .photo-credits a:hover,
  .header-default .eyebrow a, .toc-link, .article-img .img-caption a:hover,
  .link-list a, .news-list-container .h3 a:hover, .nav-newer a:hover,
  .nav-older a:hover, .elsewhere-wikimedia .link-external a:hover,
  .article-footer .read-more a, .translation-bar a:hover,
  a.hover-img-zoom:hover .person-name {
    color: var(--base-color);
  }
  .article-footer .read-more a:hover {
    color: var(--base-color);
    opacity: .9;
  }
  .header-default .ungrid .h2.eyebrow, .framing-copy-ungrid code,
  .card .post-meta a {
    color: var(--blue-1);
  }
  .card .post-meta a:hover {
    color: var(--blue-1);
    border-color: var(--blue-1);
    opacity: .9;
  }
  .framing-copy-ungrid .ungrid-line {
    background: var(--blue-1);
  }
  .btn-blue .btn, .btn.btn-blue {
    background-color: var(--blue-1);
    color: var(--gray-e);
  }
  .btn-blue .btn:hover, .btn.btn-blue:hover {
    background-color: var(--blue-1);
    color: var(--gray-e);
  }
  .fact-inner {
    background: var(--pink-0);
  }
  .connect-container .module-mu.gray-module, .bg-ltgray {
    background: var(--gray-28);
  }
  .connect-container .module-mu {
    border: 1px solid var(--gray-4);
  }
  input, textarea {
    border: 1px solid var(--gray-5);
  }
  .connect-container .module-mu .email-signup input:focus {
    background: var(--gray-1);
    outline: 1px solid var(--base-color);
    box-shadow: 0 0 1px 1px var(--base-color);
  }
  .header-default .ungrid,
  .header-home.featured-photo--content-left .vision_container {
    background: var(--brown-4);
  }
  footer.bg-black {
    background: var(--gray-3);
  }
  footer #menu-footer-under-text li a, .module-mu h4.category,
  .module-mu h4 a.category {
    background: var(--gray-28);
    color: var(--base-color);
  }
  footer #menu-footer-under-text li a:hover {
    box-shadow: 0 2px 10px 0 var(--gray-69);
  }
  .module-mu h4.category:hover, .module-mu h4 a.category:hover {
    background: var(--blue-1);
    color: var(--gra-e);
  }
  .translation-bar {
    color: var(--base-color);
    border-color: var(--gray-5);
  }
  .urgent-header {
    background: var(--red-3);
  }

sourceCode:
/* ==UserStyle==
@name         Wikipedia Dark
@namespace    StylishThemes
@version      3.4.28
@description  Wikipedia Dark theme
@author       StylishThemes
@homepageURL  https://github.com/StylishThemes/Wikipedia-Dark
@supportURL   https://github.com/StylishThemes/Wikipedia-Dark/issues/new/choose
@updateURL    https://raw.githubusercontent.com/StylishThemes/Wikipedia-Dark/master/wikipedia-dark.user.css
@license      CC-BY-SA-4.0
@advanced color     base-color "Base color"    #4183C4
@advanced color      tab-color "Tab color"     #e08b26
@advanced color  visited-color "Visited color" #295887
@advanced dropdown bg-selected "Background image" {
  crosshatch "Cross-Hatch" <<<EOT
  url('data:image/png;base64,') EOT;
  uniform "Uniform Tile" <<<EOT
  url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAABkAQMAAABKLAcXAAAABlBMVEUfHx8aGhrh02scAAAAH0lEQVR4AWMQRAICeHn/kcAH8vSN6hvVN6pvVN+oPgAPQ4LItqOMdQAAAABJRU5ErkJggg==') EOT;
  random "Random Tile" <<<EOT
  url('data:image/png;base64,') EOT;
  custom "Custom" <<<EOT
  var(--bg-custom) EOT;
  none "None" <<<EOT
  none EOT;
}
@advanced text bg-custom "Custom background; include url()" "url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAMAAAAMCGV4AAAAD1BMVEUREREREREAAAAREREWFhkN5cwCAAAABHRSTlNfGAAwFBG7CgAAACxJREFUeAFjYGJiZGBhhhMMYIIRTjCgcIE8VC4TKpeRmYEZiTtIzGNihhNMAJJAATdKr5xFAAAAAElFTkSuQmCC')"
@advanced dropdown bg-options "Background image type" {
  Tiled "Tiled" <<<EOT
  background-repeat: repeat !important;
  background-size: auto !important;
  background-position: left top !important; EOT;
  Fit "Fit Window" <<<EOT
  background-repeat: no-repeat !important;
  background-size: cover !important;
  background-position: center top !important; EOT;
}
@advanced dropdown bg-attachment "Background image attachment" {
  Scroll "Scroll" <<<EOT
  scroll EOT;
  Fixed "Fixed" <<<EOT
  fixed EOT;
}
@preprocessor uso
==/UserStyle== */
@-moz-document
regexp("https?:\/\/.*wik(i|t).*(org|jp).*") {
  /* Global color palette */
  :root {
    --base-color: /*[[base-color]]*/;
    --base-color-rgb: /*[[base-color-rgb]]*/;
    --base-color-rgba-3: rgba(var(--base-color-rgb), 3);
    --base-color-rgba-8: rgba(var(--base-color-rgb), 8);
    --tab-color: /*[[tab-color]]*/;
    --visited-color: /*[[visited-color]]*/;
    --bg-custom: /*[[bg-custom]]*/;
    --bg-selected: /*[[bg-selected]]*/;
    --bg-attachment: /*[[bg-attachment]]*/;
    /* base palette */
    --black: #000;
    --gray-1: #111;
    --gray-2: #222;
    --gray-3: #333;
    --gray-4: #444;
    --gray-5: #555;
    --gray-7: #777;
    --gray-a: #aaa;
    --gray-c: #ccc;
    --gray-d: #ddd;
    --gray-e: #eee;
    --white: #fff;
    --gray-18: #181818;
    --gray-28: #282828;
    --gray-2f: #2f4f4f;
    --gray-69: #696969;
    --gray-9a: #9a9a9a;
    --gray-b9: #b9b9b9;
    --gray-bc: #bcbcbc;
    --blue: #00f;
    --blue-1: #36c;
    --blue-2: #182030;
    --blue-3: #212c43;
    --blue-4: #3c5487;
    --red: #f00;
    --red-0: #400;
    --red-1: #c22;
    --red-2: #d33;
    --red-3: #722;
    --red-rgb: rgba(119, 34, 34, .4);
    --red-4: #ff4242;
    --green: #0f0;
    --green-1: #390;
    --green-2: #165316;
    --green-3: #061;
    --green-4: #002800;
    --brown-1: #321;
    --brown-2: #542;
    --brown-3: #261d08;
    --brown-4: #4b380d;
    --yellow-1: #b28200;
    --yellow-2: #ffdd7f;
    --orange-1: #e53e00;
    --orange-2: #e08b26;
    --purple-d: #241738;
    --purple-0: #312641;
    --purple-1: #382b4a;
    --purple-2: #768;
    --pink-0: #3e243c;
    --pink-1: #6b3e68;
    --pink-2: #965691;
    --pink-3: #ffbefa;
    --cyan-1: #194d41;
    --ores-1: #222800;
    --ores-2: #222000;
    --ores-3: #221800;
    --black_17: rgba(0, 0, 0, .175);
    --black_2: rgba(0, 0, 0, .2);
    --black_25: rgba(0, 0, 0, .25);
    --black_5: rgba(0, 0, 0, .5);
    --black_8: rgba(0, 0, 0, .8);
    --gray-2_75: rgba(34, 34, 34, .75);
    --gray-3_5: rgba(51, 51, 51, .5);
    --gray-5_25: rgba(85, 85, 85, .25);
    --gray-6_5: rgba(100, 100, 100, .5);
    --white_1: rgba(255, 255, 255, .1);
    --white_25: rgba(255, 255, 255, .25);
    --diff-del: #c22;
    --diff-add: #36c;
    --revs-old: #b28200;
    --revs-new: #070;
    /* assessment colors */
    --assess-1: #06c;
    --assess-2: #29c;
    --assess-3: #070;
    --assess-4: #6b0;
    --assess-5: #993;
    --assess-6: #b60;
    --assess-7: #921; /* was #901 */
    --assess-8: #85f;
    --assess-9: #070;
    --assess-10: #0b6;
    --assess-11: #a4c;
    --assess-12: #568;
    /* meta wiki */
    --green-a0: #2f4500;
    --green-a1: #27560f;
    --green-a2: #016101;
    --green-a3: #305229;
    --green-a4: #50612b;
    --green-a5: #676702;
    --bluei-a0: #014523;
    --bluei-a1: #004e4e;
    --bluei-a2: #05493b;
    --bluei-a3: #1b3a2a;
    /* WD query service */
    --cm-0: #141414;
    --cm-1: #27282e;
    --cm-2: #f9ee98;
    --cm-3: #fc0;
    --cm-4: #ca7841;
    --cm-5: #8da6ce;
    --cm-6: #607392;
    --cm-7: #cda869;
    --cm-8: #8f9d6a;
    --cm-9: #bd6b18;
    --cm-10: #f7f7f7;
    --cm-11: #997643;
    --cm-12: #d6bb6d;
    --cm-13: #ff6400;
    --cm-14: #aeaeae;
    --cm-15: #ad9361;
  }
  .cm-comment, .cm-link {
    font-style: italic;
  }
  .CodeMirror {
    background: var(--gray-18);
  }
  .CodeMirror-gutters {
    background: var(--gray-2);
    border-right: 1px solid var(--gray-4);
  }
  .CodeMirror-guttermarker {
    color: var(--white);
  }
  .CodeMirror-guttermarker-subtle, .CodeMirror-linenumber {
    color: var(--gray-a);
  }
  .CodeMirror-cursor {
    border-left: 2px solid var(--white);
  }
  .CodeMirror-activeline-background {
    background: var(--cm-1);
  }
  .CodeMirror-matchingbracket {
    outline: 1px solid var(--gray);
    color: var(--white);
  }
  .CodeMirror-line::selection, .CodeMirror-line > span::selection,
  .CodeMirror-line > span > span::selection {
    background: var(--cm-1);
  }
  .cm-keyword {
    color: var(--cm-2);
  }
  .cm-atom {
    color: var(--cm-3);
  }
  .cm-number {
    color: var(--cm-4);
  }
  .cm-def, .cm-mw-section-header, .cm-mw-link-pagename, .cm-mw-link-bracket,
  .cm-mw-link-delimiter, .cm-mw-indenting, .cm-mw-extlink-bracket, .cm-mw-list,
  .cm-mw-apostrophes-bold, .cm-mw-apostrophes-italic, .cm-mw-link-tosection {
    color: var(--cm-5);
  }
  .cm-mw-skipformatting {
    background-color: var(--cm-6);
  }
  .cm-variable-2, .cm-variable-3, span.cm-def, span.cm-tag,
  .cm-mw-extlink-protocol, .cm-mw-free-extlink-protocol, .cm-mw-extlink,
  .cm-mw-free-extlink {
    color: var(--cm-6);
  }
  .cm-builtin, .cm-operator {
    color: var(--cm-7);
  }
  .cm-comment {
    color: var(--gray-7);
    font-weight: 400;
  }
  .cm-string, .cm-mw-htmltag-name, .cm-mw-htmltag-bracket, .cm-mw-exttag-name,
  .cm-mw-exttag-bracket, .cm-mw-htmltag-attribute, .cm-mw-exttag-attribute,
  .cm-mw-mnemonic {
    color: var(--cm-8);
  }
  .cm-string-2 {
    color: var(--cm-9);
  }
  .cm-meta {
    background-color: var(--gray-18);
    color: var(--cm-10);
  }
  .cm-tag {
    color: var(--cm-11);
  }
  .cm-attribute {
    color: var(--cm-12);
  }
  .cm-header, .cm-mw-table-bracket, .cm-mw-table-definition,
  .cm-mw-table-delimiter {
    color: var(--cm-13);
  }
  .cm-hr {
    color: var(--cm-14);
  }
  .cm-link {
    color: var(--cm-15);
    text-decoration: none;
  }
  .cm-error {
    border-bottom: 1px solid var(--red);
  }
  .cm-mw-template-name, .cm-mw-template-bracket, .cm-mw-template,
  .cm-mw-template-argument-name, .cm-mw-template-delimiter {
    color: var(--cm-10);
  }
  /*# sourceURL=ace/css/ace-tm */
  .ace-tm .ace_gutter {
    background: var(--cm-1);
    color: var(--cm-10);
  }
  .ace-tm .ace_print-margin {
    background: var(--cm-1);
  }
  .ace-tm .ace_fold {
    background-color: var(--cm-6);
  }
  .ace-tm {
    background-color: var(--cm-0);
    color: var(--cm-10);
  }
  .ace-tm .ace_cursor {
    color: var(--cm-10);
  }
  .ace-tm .ace_invisible {
    color: var(--cm-10);
  }
  .ace-tm .ace_storage, .ace-tm .ace_keyword {
    color: var(--cm-5);
  }
  .ace-tm .ace_constant {
    color: var(--cm-13);
  }
  .ace-tm .ace_constant.ace_buildin {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_language {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_library {
    color: var(--cm-8);
  }
  .ace-tm .ace_invalid {
    background-color: rgba(255, 0, 0, .1);
    color: var(--cm-13);
  }
  .ace-tm .ace_support.ace_function {
    color: var(--cm-5);
  }
  .ace-tm .ace_support.ace_constant {
    color: var(--cm-8);
  }
  .ace-tm .ace_support.ace_type, .ace-tm .ace_support.ace_class {
    color: var(--cm-14);
  }
  .ace-tm .ace_keyword.ace_operator {
    color: var(--cm-14);
  }
  .ace-tm .ace_string {
    color: var(--cm-8);
  }
  .ace-tm .ace_comment {
    color: var(--cm-14);
  }
  .ace-tm .ace_comment.ace_doc {
    color: var(--cm-5);
  }
  .ace-tm .ace_comment.ace_doc.ace_tag {
    color: var(--cm-14);
  }
  .ace-tm .ace_constant.ace_numeric {
    color: var(--cm-5);
  }
  .ace-tm .ace_variable {
    color: var(--cm-12);
  }
  .ace-tm .ace_xml-pe {
    color: var(--cm-11);
  }
  .ace-tm .ace_entity.ace_name.ace_function {
    color: var(--cm-5);
  }
  .ace-tm .ace_heading {
    color: var(--cm-5);
  }
  .ace-tm .ace_list {
    color: var(--cm-13);
  }
  .ace-tm .ace_meta.ace_tag {
    color: var(--cm-6);
  }
  .ace-tm .ace_string.ace_regex {
    color: var(--cm-13);
  }
  .ace-tm .ace_marker-layer .ace_selection {
    background: var(--cm-1);
  }
  .ace-tm.ace_multiselect .ace_selection.ace_start {
    box-shadow: 0 0 3px 0 var(--cm-10);
  }
  .ace-tm .ace_marker-layer .ace_step {
    background: var(--cm-3);
  }
  .ace-tm .ace_marker-layer .ace_stack {
    background: var(--cm-2);
  }
  .ace-tm .ace_marker-layer .ace_bracket {
    border: 1px solid var(--cm-14);
  }
  .ace-tm .ace_marker-layer .ace_active-line {
    background: rgba(0, 0, 0, .07);
  }
  .ace-tm .ace_gutter-active-line {
    background-color: var(--gray-3);
  }
  .ace-tm .ace_marker-layer .ace_selected-word {
    background: var(--gray-3);
    border: 1px solid var(--cm-11);
  }
  .ace-tm .ace_indent-guide {
    background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAE0lEQVQImWP4////f4bLly//BwAmVgd1/w11/gAAAABJRU5ErkJggg==") right repeat-y;
  }
  .ve-ui-mwAceEditorWidget .ace_editor {
    border-color: var(--cm-6);
  }
}
@-moz-document
regexp("https?:\/\/(([\w\-]{2,}\.)?(wiki(pedia|books|news|quote|source|versity|voyage)|wiktionary)|(www|test)\.(mediawiki|wiki(data|dot))|(meta|wikimania|commons|otrs-wiki|species|wikitech|incubator|outreach|foundation|donate)\.wikimedia)\.org\/.*"),
regexp("https?:\/\/wiki\.(archlinux|mozilla)\.(org|jp)\/.*$") {
  /* transparent background */
  html, body, .mw-body[role="main"], .skin-vector-max-width .mw-page-container,
  .skin-vector-max-width #mw-panel, .mw-page-container {
    background-color: var(--gray-2);
    background-image: var(--bg-selected);
    background-clip: border-box;
    background-origin: padding-box;
    background-attachment: var(--bg-attachment);
    /*[[bg-options]]*/
    color: var(--gray-c);
  }
  .mw-body, .parsoid-body, #mp-topbanner,
  span:not(.legend-color):not([class*="wikEd"]):not([style*="background"]):not(.wikibase-toolbar-wrapper):not(.tool),
  .navbox-list, .mw-wsmfinal-content, .mw-body-content,
  .referencetooltip > li + li, div.vectorTabs ul li,
