    border-color: #555 !important;
  }
  a[id*="_toggle_link"]:hover {
    background-color: transparent !important;
  }
  div[id*="_paged_list"] a, .HoverMenu .TopicNameSpan.TopicName,
  .PrivacySettings .ts_gutter_header {
    color: #ccc !important;
  }
